}

/*______________ Gestion avatar / user profile (menu) ________________*/

.comm-user-profile-menu .slds-avatar img,
.profile-icon {
    content: url(/sfsites/c/file-asset/profileIconOrange) !important;
    min-width: 18px !important;
    height: fit-content !important;
}
.slds-avatar {
    position: relative;
    top: -3px;
}

/* Gradiant on avatar div */
.slds-dropdown-trigger.slds-dropdown-trigger_click.slds-grid {
    background: linear-gradient(90deg, rgba(226, 123, 71, 1) 0%, rgba(254, 211, 138, 1) 100%);
    border-radius: 5px;
    padding: 1px;
}

button.slds-button.slds-no-space.slds-m-bottom_xx-small {
    background: white;
    padding: 5px 10px;
    height: 42px;
    margin: 0;
    transition: all 0.3s;
}

/* hover effect on avatar div */
button.slds-button.slds-no-space.slds-m-bottom_xx-small:hover {
    background: black;
    color: white;
}

button.slds-button.slds-no-space.slds-m-bottom_xx-small:hover p {
    color: white !important;
}

/* box shadow on dropdown user settings menu */
.comm-user-profile-menu__dropdown-ctn.slds-dropdown.slds-dropdown_right.slds-nubbin_top-right {
    box-shadow: 0 0px 4px 0 rgb(0 0 0 / 16%) !important;
}

/* user wrapper */
.themeUtils {
    top: 25px;
}

/* notification svg */
.forceCommunityThemeHeaderCompact .themeUtils .themeUtil.themeNotifications button svg,
.themeNotifications {
    display: none !important;
}

.forceCommunityThemeHeaderCompact .themeUtils .themeUtil svg {
    height: 31px !important;
    width: 18px !important;
}

/* search svg / close search svg */
