/* ___________________ Gestion tailles écran (desktop) pour menu ______________________ */

@media screen and (max-width: 1300px) {

    /* center menu */
    .themeNav {
        margin-left: 0 !important;
    }
}

@media screen and (min-width: 768px) and (max-width: 1559px) {

    /* remove username */
    .slds-media__body.slds-show_medium.comm-user-profile-menu__trigger {
        display: none !important;
    }

    /* reduce logo size */
    .logoImage {
        width: 116px !important;
    }

    /* position of logo */
    .forceCommunityThemeHeaderCompact .themeLogo {
        top: 20px !important;
    }
}

@media screen and (min-width: 768px) and (max-width: 1100px) {

    /* reduce menu items font-size */
    /*.themeNav nav li a,
    .themeNav nav li button,
    .themeNav nav li a:hover {
        font-size: 0.7rem !important;
    }*/

    /* remove padding on menu items */
    .themeNav nav li {
        padding-left: 0 !important;
        padding-right: 0 !important;
    }

